UPDATE `script_texts` SET `content_loc8`='Они упрямы.' WHERE `npc_entry`='26499' AND `entry`='-1595107';
UPDATE `script_texts` SET `content_loc8`='Что еще он поставит у меня на пути?' WHERE `npc_entry`='26499' AND `entry`='-1595108';
