UPDATE `script_texts` SET `content_loc8`='Давайте пройдем этот участок как можно быстрее. Если мы не погибнем от нежити, то можем погибнуть от этого огня.' WHERE `npc_entry`='26499' AND `entry`='-1595113';
UPDATE `script_texts` SET `content_loc8`='Отдышитесь немного. Но имейте в виду, нам скоро снова в путь.' WHERE `npc_entry`='26499' AND `entry`='-1595114';
UPDATE `script_texts` SET `content_loc8`='Отдых окончен, надо идти. Мал\'Ганис ждет.' WHERE `npc_entry`='26499' AND `entry`='-1595115';
